package primer_proyecto;

public class bucles {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i=0;
		 while (i<10) {
			 System.out.print(i++ +" ");
			
		 }
		 System.out.println("");
		 do {
			 System.out.print(i++ +" ");
		 }while(i<10);
	}

}
